import PropTypes from 'prop-types';
import { PureComponent } from 'react';

import './ProgressBar.style';

import {
    LEFT,
    RIGHT
} from './ProgressBar.config'

const PROGRESS_POINT_WIDTH = 35; // px
const PROGRESS_POINT_MARGIN = 5; // px

/** @namespace Component/ProgressBar/Component */
export class ProgressBar extends PureComponent {
    static propTypes = {
        progressList: PropTypes.arrayOf(PropTypes.string),
        currentID: PropTypes.number
    };

    renderEdgeLine(side) {
        const {
            progressList
        } = this.props;
        return (
            <div
              block={`Bar ${side}EdgeLine`}
              style={{ width: `calc(${1 / (progressList.length + 1)} - ${PROGRESS_POINT_WIDTH}px` }}
            />
        );
    }

    // renderName() {
    //     const { name } = this.props;

    //     return (
    //         <TextPlaceholder content={ name } />
    //     );
    // }

    render() {
        return (
            <div
                block="ProgressBar"
            >
                { this.renderEdgeLine(LEFT) }
                { this.renderEdgeLine(RIGHT) }
            </div>
        );
    }
}

export default ProgressBar;
