import PropTypes from 'prop-types';
import { PureComponent } from 'react';

import './ProgressBar.style';

import {
    LEFT,
    RIGHT
} from './ProgressBar.config'

/** @namespace Component/ProgressBar/Component */
export class ProgressBar extends PureComponent {
    static propTypes = {
        progressList: PropTypes.arrayOf(PropTypes.string),
        currentID: PropTypes.number,
        accentColor: PropTypes.string
    };

    static defaultProps = {
        accentColor: "rgb(219,219,219)"
    };

    renderEdgeBar() {
        const {
            index,
            isDisabled
        } = this.props;

        const url = this.getLinkUrl() || {};

        return (
            <Link
              block="Breadcrumb"
              elem="Link"
              to={ url }
              tabIndex={ isDisabled ? '-1' : '0' }
            >
                <meta itemProp="item" content={ window.location.origin + url.pathname } />
                <span itemProp="name">
                    { this.renderName() }
                </span>
                <meta itemProp="position" content={ index } />
            </Link>
        );
    }

    // renderName() {
    //     const { name } = this.props;

    //     return (
    //         <TextPlaceholder content={ name } />
    //     );
    // }

    render() {
        return (
            <div
                block="ProgressBar"
            >
                { renderEdgeBar(LEFT) }
                {  }
                { renderEdgeBar(RIGHT) }
            </div>
        );
    }
}

export default ProgressBar;
